def worker():
    while True:
        current_time = datetime.now().strftime('%H:%M')
        with open('user_settings.json', encoding='utf-8') as json_file:
            data = json.load(json_file)
        if current_time == data['time']:
            id_photo = get_file_name()
            if id_photo is not None:
                id = randint(0, 148)
                with open('user_settings.json', 'w', encoding='utf-8') as outfile:
                    json.dump(data, outfile)
                bot.send_photo(int(os.environ['CHAT_ID']), id_photo, caption=ARR[id], reply_markup=keyboard1)
            time.sleep(61)

def proc_start(worker):
    '''Запускает процесс'''
    p_to_start = Process(target=worker, name="worker")
    p_to_start.start()
    return p_to_start

